import React, { useState, useRef, useEffect } from 'react';
import { GoogleMap, useJsApiLoader, Marker, Autocomplete, DirectionsRenderer } from '@react-google-maps/api';

// ##################################################################
// # COLOQUE SUAS CHAVES E IPs AQUI                                 #
// ##################################################################

// 1. CHAVE DA API GOOGLE MAPS PLATFORM
//    Ative as APIs: "Maps JavaScript API", "Directions API", "Places API"
//    Instruções: https://developers.google.com/maps/gmp-get-started
const GOOGLE_MAPS_API_KEY = "AIzaSyAVEzH5bvFPDpgjQtnA4s2TKfjEW_0OTLY";

// 2. ENDEREÇO IP DO ESP32
//    Este é o IP que o ESP32 receberá na sua rede Wi-Fi.
const ESP32_IP_ADDRESS = "192.168.1.12"; // Exemplo: "192.168.1.100"

// ##################################################################

const containerStyle = {
  width: '100%',
  height: '100%'
};

const libraries: "places"[] = ["places"];

function App() {
  const { isLoaded } = useJsApiLoader({
    id: 'google-map-script',
    googleMapsApiKey: GOOGLE_MAPS_API_KEY,
    libraries,
  });

  const [map, setMap] = useState<google.maps.Map | null>(null);
  const [directionsResponse, setDirectionsResponse] = useState<google.maps.DirectionsResult | null>(null);
  const [currentPosition, setCurrentPosition] = useState<google.maps.LatLngLiteral | null>(null);
  const [simplifiedInstructions, setSimplifiedInstructions] = useState<string[]>([]);
  
  const destinationRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const pos = {
            lat: position.coords.latitude,
            lng: position.coords.longitude,
          };
          setCurrentPosition(pos);
          map?.panTo(pos);
        },
        () => {
          alert("Erro: O serviço de geolocalização falhou.");
        }
      );
    } else {
      alert("Erro: Seu navegador não suporta geolocalização.");
    }
  }, [map]);

  const calculateRoute = async () => {
    if (!destinationRef.current?.value || !currentPosition) {
      return;
    }
    
    const directionsService = new google.maps.DirectionsService();
    const results = await directionsService.route({
      origin: currentPosition,
      destination: destinationRef.current.value,
      travelMode: google.maps.TravelMode.DRIVING
    });

    setDirectionsResponse(results);
    generateSimplifiedInstructions(results);
  };
  
  const generateSimplifiedInstructions = (directions: google.maps.DirectionsResult) => {
    if (!directions.routes || directions.routes.length === 0) {
      return;
    }
    const route = directions.routes[0].legs[0];
    const instructions = route.steps.map((step: google.maps.DirectionsStep) => {
      // Remove tags HTML da instrução (ex: <b>, <div>)
      const cleanInstruction = step.instructions.replace(/<[^>]*>/g, '');
      const distance = step.distance?.text || '';
      return `${cleanInstruction} (${distance})`;
    });
    setSimplifiedInstructions(instructions);
  };

  const sendInstructionsToESP32 = async () => {
    if (simplifiedInstructions.length === 0) {
      alert("Nenhuma instrução para enviar. Primeiro, calcule uma rota.");
      return;
    }

    const endpoint = `http://${ESP32_IP_ADDRESS}/navigate`;
    console.log(`Enviando ${simplifiedInstructions.length} instruções para ${endpoint}`);

    try {
      const response = await fetch(endpoint, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ instructions: simplifiedInstructions }),
      });

      if (response.ok) {
        alert("Instruções enviadas com sucesso para o ESP32!");
      } else {
        throw new Error(`Falha na resposta do servidor: ${response.status}`);
      }
    } catch (error) {
      console.error("Erro ao enviar dados para o ESP32:", error);
      alert(`Não foi possível conectar ao ESP32 em ${ESP32_IP_ADDRESS}. Verifique o IP e a conexão Wi-Fi.`);
    }
  };

  if (!isLoaded) {
    return <div>Carregando Mapa...</div>;
  }

  return (
    <div className="App">
      <div className="map-container">
        <GoogleMap
          mapContainerStyle={containerStyle}
          center={currentPosition || { lat: -23.55052, lng: -46.633308 }} // Fallback to São Paulo
          zoom={15}
          onLoad={(map: google.maps.Map) => setMap(map)}
          options={{disableDefaultUI: true, zoomControl: true}}
        >
          {currentPosition && <Marker position={currentPosition} />}
          {directionsResponse && <DirectionsRenderer directions={directionsResponse} />}
        </GoogleMap>
      </div>
      
      <div className="controls-container">
        <Autocomplete>
          <input
            type='text'
            placeholder='Digite o endereço de destino'
            ref={destinationRef}
          />
        </Autocomplete>
        <button onClick={calculateRoute}>Gerar Rota</button>
        <button onClick={sendInstructionsToESP32}>Enviar para ESP32</button>
      </div>

      <div className="instructions">
        <h3>Instruções de Navegação</h3>
        <ul>
          {simplifiedInstructions.map((inst, index) => (
            <li key={index}>{inst}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
