import React from 'react';
import { useOffline } from '../hooks/useOffline';

interface OfflineStatusProps {
  className?: string;
}

export const OfflineStatus: React.FC<OfflineStatusProps> = ({ className }) => {
  const { 
    isOnline, 
    isInstalled, 
    showInstallPrompt, 
    installPWA,
    syncData 
  } = useOffline();

  return (
    <div className={`offline-status ${className || ''}`}>
      {/* Status Online/Offline */}
      <div className={`status-indicator ${isOnline ? 'online' : 'offline'}`}>
        <span className="status-dot"></span>
        <span className="status-text">
          {isOnline ? 'Online' : 'Offline'}
        </span>
      </div>

      {/* BotÃ£o de InstalaÃ§Ã£o PWA */}
      {showInstallPrompt && !isInstalled && (
        <button 
          className="install-pwa-btn"
          onClick={installPWA}
          title="Instalar Castrilha"
        >
          ðŸ“± Instalar App
        </button>
      )}

      {/* BotÃ£o de SincronizaÃ§Ã£o */}
      {isOnline && (
        <button 
          className="sync-btn"
          onClick={syncData}
          title="Sincronizar dados"
        >
          ðŸ”„ Sincronizar
        </button>
      )}

      {/* Indicador de PWA Instalado */}
      {isInstalled && (
        <div className="pwa-installed">
          <span>ðŸ“± App Instalado</span>
        </div>
      )}
    </div>
  );
};
